automatedScript:
# Azure Repository Security Assessment Script (2024)
param(
    [Parameter(Mandatory=$true)]
    [string]$Organization,
    [Parameter(Mandatory=$true)]
    [string]$Project,
    [Parameter(Mandatory=$false)]
    [string]$Repository,
    [Parameter(Mandatory=$true)]
    [string]$PersonalAccessToken
)

Write-Host "=== Azure Repository Security Assessment ===" -ForegroundColor Cyan
Write-Host "Organization: $Organization" -ForegroundColor White
Write-Host "Project: $Project" -ForegroundColor White
if ($Repository) { Write-Host "Repository: $Repository" -ForegroundColor White }

# Setup authentication headers
$base64AuthInfo = [Convert]::ToBase64String([Text.Encoding]::ASCII.GetBytes(":$PersonalAccessToken"))
$headers = @{
    Authorization = "Basic $base64AuthInfo"
    'Content-Type' = 'application/json'
}

# Branch Protection Policy Checks
Write-Host "`n--- Branch Protection Policies ---" -ForegroundColor Yellow
try {
    $policiesUri = "https://dev.azure.com/$Organization/$Project/_apis/policy/configurations?api-version=7.1-preview.1"
    $policies = Invoke-RestMethod -Uri $policiesUri -Headers $headers -Method Get

    $reviewerPolicies = $policies.value | Where-Object { $_.type.displayName -eq "Minimum number of reviewers" }
    $statusCheckPolicies = $policies.value | Where-Object { $_.type.displayName -eq "Build" }

    if ($reviewerPolicies.Count -gt 0) {
        Write-Host "‚úÖ Branch protection policies found" -ForegroundColor Green
        foreach ($policy in $reviewerPolicies) {
            $repoId = $policy.settings.scope[0].repositoryId
            $minReviewers = $policy.settings.minimumApproverCount
            $resetOnPush = $policy.settings.resetOnSourcePush

            Write-Host "   Repository ID: $repoId" -ForegroundColor White
            Write-Host "   Minimum Reviewers: $minReviewers" -ForegroundColor White
            Write-Host "   Reset on Source Push: $resetOnPush" -ForegroundColor White

            if ($minReviewers -lt 2) {
                Write-Host "   ‚ùå Insufficient reviewer requirement (should be 2+)" -ForegroundColor Red
            } else {
                Write-Host "   ‚úÖ Adequate reviewer requirement" -ForegroundColor Green
            }
        }
    } else {
        Write-Host "‚ùå No minimum reviewer policies found" -ForegroundColor Red
    }

    if ($statusCheckPolicies.Count -gt 0) {
        Write-Host "‚úÖ Build validation policies found: $($statusCheckPolicies.Count)" -ForegroundColor Green
    } else {
        Write-Host "‚ö†Ô∏è No build validation policies found" -ForegroundColor Yellow
    }
} catch {
    Write-Host "‚ùå Error checking branch policies: $($_.Exception.Message)" -ForegroundColor Red
}

# Repository Security Features Check
Write-Host "`n--- Repository Security Features ---" -ForegroundColor Yellow
if ($Repository) {
    try {
        # Check if GitHub Advanced Security is enabled
        $repoUri = "https://dev.azure.com/$Organization/$Project/_apis/git/repositories/$Repository?api-version=7.1-preview.1"
        $repoDetails = Invoke-RestMethod -Uri $repoUri -Headers $headers -Method Get

        # Check Advanced Security enablement (requires specific API)
        $advSecUri = "https://dev.azure.com/$Organization/$Project/_apis/management/repositories/$Repository/enablement?api-version=7.1-preview.1"
        try {
            $advSecurity = Invoke-RestMethod -Uri $advSecUri -Headers $headers -Method Get
            if ($advSecurity.advancedSecurity.isEnabled) {
                Write-Host "‚úÖ GitHub Advanced Security is enabled" -ForegroundColor Green

                # Check secret scanning alerts
                $secretAlertsUri = "https://dev.azure.com/$Organization/$Project/_apis/management/repositories/$Repository/alerts?alertType=secret&api-version=7.1-preview.1"
                try {
                    $secretAlerts = Invoke-RestMethod -Uri $secretAlertsUri -Headers $headers -Method Get
                    if ($secretAlerts.value.Count -gt 0) {
                        Write-Host "‚ùå $($secretAlerts.value.Count) secret scanning alert(s) found" -ForegroundColor Red
                        foreach ($alert in $secretAlerts.value | Select-Object -First 5) {
                            Write-Host "   ‚Ä¢ $($alert.rule.name) in $($alert.physicalLocations[0].artifactLocation.uri)" -ForegroundColor White
                        }
                    } else {
                        Write-Host "‚úÖ No secret scanning alerts" -ForegroundColor Green
                    }
                } catch {
                    Write-Host "‚ö†Ô∏è Could not check secret scanning alerts" -ForegroundColor Yellow
                }

                # Check dependency scanning alerts
                $depAlertsUri = "https://dev.azure.com/$Organization/$Project/_apis/management/repositories/$Repository/alerts?alertType=dependency&api-version=7.1-preview.1"
                try {
                    $depAlerts = Invoke-RestMethod -Uri $depAlertsUri -Headers $headers -Method Get
                    if ($depAlerts.value.Count -gt 0) {
                        Write-Host "‚ùå $($depAlerts.value.Count) dependency vulnerability alert(s) found" -ForegroundColor Red
                        foreach ($alert in $depAlerts.value | Select-Object -First 5) {
                            Write-Host "   ‚Ä¢ $($alert.rule.name) - Severity: $($alert.rule.properties.severity)" -ForegroundColor White
                        }
                    } else {
                        Write-Host "‚úÖ No dependency vulnerability alerts" -ForegroundColor Green
                    }
                } catch {
                    Write-Host "‚ö†Ô∏è Could not check dependency scanning alerts" -ForegroundColor Yellow
                }
            } else {
                Write-Host "‚ùå GitHub Advanced Security is not enabled" -ForegroundColor Red
            }
        } catch {
            Write-Host "‚ö†Ô∏è Could not check Advanced Security status" -ForegroundColor Yellow
        }
    } catch {
        Write-Host "‚ùå Error checking repository security: $($_.Exception.Message)" -ForegroundColor Red
    }
} else {
    Write-Host "‚ö†Ô∏è No specific repository provided - skipping repository-specific checks" -ForegroundColor Yellow
}

# Service Connections Security Check
Write-Host "`n--- Service Connections Security ---" -ForegroundColor Yellow
try {
    $serviceConnectionsUri = "https://dev.azure.com/$Organization/$Project/_apis/serviceendpoint/endpoints?api-version=7.1-preview.4"
    $serviceConnections = Invoke-RestMethod -Uri $serviceConnectionsUri -Headers $headers -Method Get

    if ($serviceConnections.value.Count -gt 0) {
        Write-Host "Found $($serviceConnections.value.Count) service connection(s)" -ForegroundColor White
        foreach ($connection in $serviceConnections.value) {
            Write-Host "   Connection: $($connection.name) (Type: $($connection.type))" -ForegroundColor White

            # Check for overprivileged connections
            if ($connection.type -eq "azurerm") {
                if ($connection.data.subscriptionId) {
                    Write-Host "   Azure Subscription: $($connection.data.subscriptionId)" -ForegroundColor White
                    if ($connection.data.scopeLevel -eq "Subscription") {
                        Write-Host "   ‚ö†Ô∏è Subscription-level access (consider resource group scope)" -ForegroundColor Yellow
                    }
                }
            }
        }
    } else {
        Write-Host "‚úÖ No service connections found" -ForegroundColor Green
    }
} catch {
    Write-Host "‚ùå Error checking service connections: $($_.Exception.Message)" -ForegroundColor Red
}

# Pipeline Security Check
Write-Host "`n--- Pipeline Security ---" -ForegroundColor Yellow
try {
    $pipelinesUri = "https://dev.azure.com/$Organization/$Project/_apis/pipelines?api-version=7.1-preview.1"
    $pipelines = Invoke-RestMethod -Uri $pipelinesUri -Headers $headers -Method Get

    if ($pipelines.value.Count -gt 0) {
        Write-Host "Found $($pipelines.value.Count) pipeline(s)" -ForegroundColor White
        $yamlPipelines = $pipelines.value | Where-Object { $_.configuration.type -eq "yaml" }
        $classicPipelines = $pipelines.value | Where-Object { $_.configuration.type -ne "yaml" }

        Write-Host "   YAML Pipelines: $($yamlPipelines.Count)" -ForegroundColor White
        Write-Host "   Classic Pipelines: $($classicPipelines.Count)" -ForegroundColor White

        if ($classicPipelines.Count -gt 0) {
            Write-Host "   ‚ö†Ô∏è Classic pipelines found (consider migrating to YAML)" -ForegroundColor Yellow
        }

        # Check for pipeline approvals
        foreach ($pipeline in $pipelines.value | Select-Object -First 3) {
            $pipelineDetailUri = "https://dev.azure.com/$Organization/$Project/_apis/pipelines/$($pipeline.id)?api-version=7.1-preview.1"
            try {
                $pipelineDetail = Invoke-RestMethod -Uri $pipelineDetailUri -Headers $headers -Method Get
                Write-Host "   Pipeline: $($pipeline.name)" -ForegroundColor White
                Write-Host "     Path: $($pipelineDetail.configuration.path)" -ForegroundColor White
            } catch {
                Write-Host "   ‚ö†Ô∏è Could not get details for pipeline: $($pipeline.name)" -ForegroundColor Yellow
            }
        }
    } else {
        Write-Host "‚úÖ No pipelines found" -ForegroundColor Green
    }
} catch {
    Write-Host "‚ùå Error checking pipelines: $($_.Exception.Message)" -ForegroundColor Red
}

# Organization Security Settings
Write-Host "`n--- Organization Security Settings ---" -ForegroundColor Yellow
try {
    # Check organization policies
    $orgPoliciesUri = "https://dev.azure.com/$Organization/_apis/policy/configurations?api-version=7.1-preview.1"
    try {
        $orgPolicies = Invoke-RestMethod -Uri $orgPoliciesUri -Headers $headers -Method Get
        Write-Host "Organization-level policies: $($orgPolicies.value.Count)" -ForegroundColor White
    } catch {
        Write-Host "‚ö†Ô∏è Could not access organization-level policies" -ForegroundColor Yellow
    }

    # Check audit settings
    Write-Host "üìã Manual check required:" -ForegroundColor Cyan
    Write-Host "   ‚Ä¢ Navigate to Organization Settings > Audit" -ForegroundColor White
    Write-Host "   ‚Ä¢ Verify audit log retention (default: 90 days)" -ForegroundColor White
    Write-Host "   ‚Ä¢ Check audit streaming configuration" -ForegroundColor White
    Write-Host "   ‚Ä¢ Review security group memberships" -ForegroundColor White

} catch {
    Write-Host "‚ùå Error checking organization settings: $($_.Exception.Message)" -ForegroundColor Red
}

# Extensions Security Check
Write-Host "`n--- Installed Extensions Security ---" -ForegroundColor Yellow
try {
    $extensionsUri = "https://extmgmt.dev.azure.com/$Organization/_apis/extensionmanagement/installedextensions?api-version=7.1-preview.1"
    $extensions = Invoke-RestMethod -Uri $extensionsUri -Headers $headers -Method Get

    if ($extensions.value.Count -gt 0) {
        Write-Host "Found $($extensions.value.Count) installed extension(s)" -ForegroundColor White
        $riskyScopes = @("vso.code_full", "vso.build_execute", "vso.release_execute", "vso.project_manage")

        foreach ($extension in $extensions.value | Select-Object -First 10) {
            Write-Host "   Extension: $($extension.extensionName) by $($extension.publisherName)" -ForegroundColor White
            Write-Host "     Version: $($extension.version)" -ForegroundColor White

            $hasRiskyScope = $false
            foreach ($scope in $extension.scopes) {
                if ($scope -in $riskyScopes) {
                    $hasRiskyScope = $true
                    break
                }
            }

            if ($hasRiskyScope) {
                Write-Host "     ‚ö†Ô∏è Has high-risk permissions" -ForegroundColor Yellow
            } else {
                Write-Host "     ‚úÖ Standard permissions" -ForegroundColor Green
            }
        }
    } else {
        Write-Host "‚úÖ No third-party extensions installed" -ForegroundColor Green
    }
} catch {
    Write-Host "‚ùå Error checking extensions: $($_.Exception.Message)" -ForegroundColor Red
}

# Summary and Recommendations
Write-Host "`n=== Security Assessment Summary ===" -ForegroundColor Cyan
Write-Host "Organization: $Organization | Project: $Project" -ForegroundColor White
Write-Host "`nKey Security Checks:" -ForegroundColor White
Write-Host "‚úÖ Branch Protection Policies" -ForegroundColor White
Write-Host "‚úÖ Repository Security Features (Advanced Security)" -ForegroundColor White
Write-Host "‚úÖ Service Connections Configuration" -ForegroundColor White
Write-Host "‚úÖ Pipeline Security Review" -ForegroundColor White
Write-Host "‚úÖ Extensions Security Assessment" -ForegroundColor White
Write-Host "`nManual Reviews Required:" -ForegroundColor Yellow
Write-Host "‚Ä¢ Personal Access Token audit" -ForegroundColor White
Write-Host "‚Ä¢ Service Principal permissions review" -ForegroundColor White
Write-Host "‚Ä¢ Audit logging configuration" -ForegroundColor White
Write-Host "‚Ä¢ User access and permission reviews" -ForegroundColor White
Write-Host "`nNext Steps:" -ForegroundColor Cyan
Write-Host "1. Address any security findings identified above" -ForegroundColor White
Write-Host "2. Enable GitHub Advanced Security if not already active" -ForegroundColor White
Write-Host "3. Implement branch protection policies on all critical repositories" -ForegroundColor White
Write-Host "4. Configure audit streaming to SIEM/Log Analytics" -ForegroundColor White
Write-Host "5. Regular security assessments and policy reviews" -ForegroundColor White
